// src/router/index.js
import Vue from "vue";
import Router from "vue-router";
import Dashboard from "@/views/Dashboard.vue";
import Yard from "@/views/Dashboard/Yard.vue"; // Yard 컴포넌트 추가
import TransportLog from "@/views/TransportLog.vue";
import DriverProfiles from "@/views/DriverProfiles.vue";

Vue.use(Router);

export default new Router({
  routes: [
    {
      path: "/dashboard",
      component: Dashboard,
      children: [
        {
          path: "yard", // /dashboard/yard 경로에서 Yard 페이지가 표시됨
          name: "Yard",
          component: Yard,
        },
      ],
    },
    {
      path: "/transport-log",
      name: "Transport Log",
      component: TransportLog,
    },
    {
      path: "/driver-profiles",
      name: "Driver Profiles",
      component: DriverProfiles,
    },
  ],
});
